import React, { useState } from 'react';
import './UserLogin.css';
import { FaArrowLeft } from 'react-icons/fa'; // Necesitarás instalar: npm install react-icons

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);
  const [error, setError] = useState(null);
  
  const handleLogin = async (e) => {
    e.preventDefault();
    // Implementación básica
    try {
      // Aquí iría la llamada a tu API
      console.log('Iniciando sesión con:', username, password);
      // Si hay éxito, redireccionar
      // window.location.href = '/dashboard';
    } catch (err) {
      setError('Credenciales incorrectas. Por favor intente nuevamente.');
    }
  };

  return (
    <div className="modern-login-container">
      <div className="building-background"></div>
      <div className="login-card">
        <div className="login-form-side">
          <button className="back-button">
            <FaArrowLeft />
          </button>
          
          <h1 className="login-title">Acceso al Sistema</h1>
          
          <p className="login-subtitle">
            Por favor ingrese sus credenciales
            <br />o <a href="/register">haga clic aquí</a> para registrarse
          </p>
          
          {error && <div className="error-message">{error}</div>}
          
          <form onSubmit={handleLogin}>
            <div className="form-group">
              <input 
                type="text" 
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Nombre de usuario"
                className="modern-input"
                required
              />
            </div>
            
            <div className="form-group">
              <input 
                type="password" 
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Contraseña"
                className="modern-input"
                required
              />
            </div>
            
            <div className="remember-me">
              <label className="checkbox-container">
                <input 
                  type="checkbox"
                  checked={rememberMe}
                  onChange={() => setRememberMe(!rememberMe)}
                />
                <span className="checkmark"></span>
                Recordarme
              </label>
            </div>
            
            <button type="submit" className="login-btn">
              Ingresar
            </button>
          </form>
        </div>
        
        <div className="graphic-side">
          {/* El lado gráfico con gradientes */}
        </div>
      </div>
    </div>
  );
}

export default Login;
